import axios from 'axios';

const headers = {
    'Access-Control-Allow-Origin':true
};


class Api{
    signup(signupRequest){
        return axios.post('http://localhost:8080/quizapp/api/signup',signupRequest);
    }

    login(loginRequest){
        return axios.post('http://localhost:8080/quizapp/api/login',loginRequest);
    }

    getCurrentUser(){
        return axios.create({
            baseURL:"https://cors-anywhere-herokuapp.com/http://localhost:8080"
        }).get('/quizapp/api/currentuser',{headers});
    }

}


export default new Api()
