import { Button } from "antd";
import Form from "antd/lib/form/Form";
import FormItem from "antd/lib/form/FormItem";
import TextArea from "antd/lib/input/TextArea";
import { Component } from "react";
import { QUIZ_QUESTION_MAX_LENGTH } from "../common/constants";
import './NewQuiz.css';  



class NewQuiz extends Component{
    constructor(props){
        super(props);
        this.state={
            quiz:{
                text:''
            },
            questions:[{
                text:''
            }],
            choices:[{
                text:''
            },
            {
                text:''
            },
            {
                text:''
            },
            {
                text:''
            }]

        }

    this.handleQuizChange=this.handleQuizChange.bind(this);
    this.handleSubmit=this.handleSubmit.bind(this);


    }

    handleQuizChange(event){
        const value=event.target.value;
        this.setState({
            quiz:{
                text:value,
                ...this.validateQuizName(value)
            }
        })

    }

    validateQuizName=(value)=>{
        if(value.length === 0) {
            return {
                validateStatus: 'error',
                errorMsg: 'Wpisz nazwę quizu!'
            }
        } else if (value.length > QUIZ_QUESTION_MAX_LENGTH) {
            return {
                validateStatus: 'error',
                errorMsg: `Nazwa quizu za długa (Maksymalnie ${QUIZ_QUESTION_MAX_LENGTH} znaków dozwolone)`
            }    
        } else {
            return {
                validateStatus: 'success',
                errorMsg: null
            }
        }
    }

    handleSubmit(event){

    }



    render(){

        return(
            <div className="new-quiz-container">
                <h1 className="title">Nowy Quiz</h1>
                <div className="new-quiz-content">
                    <Form onFinish={this.handleSubmit} className="new-quiz-form">
                        <Form.Item validateStatus={this.state.quiz.validateStatus}
                        help={this.state.quiz.errorMsg} className="form-row">
                            <TextArea
                            placeholder="Wprowadź nazwę quizu"
                            style = {{ fontSize: '16px' }} 
                            autosize={{ minRows: 3, maxRows: 6 }} 
                            name = "quiz"
                            value = {this.state.quiz.text}
                            onChange = {this.handleQuizChange}/>
                        </Form.Item>
                        <FormItem className="poll-form-row">
                            <Button type="primary" 
                                htmlType="submit" 
                                size="large" 
                                
                                className="create-poll-form-button">Create Poll</Button>
                        </FormItem>


                    </Form>
                </div>
            </div>
        );
    }

}





export default NewQuiz;
