import axios from 'axios';
import { ACCESS_TOKEN } from '../common/constants';



class Api{
    createHeaders(){
        const headers = new Headers({
            'Content-Type': 'application/json',
        })

        if(localStorage.getItem(ACCESS_TOKEN)) {
            headers.append('Authorization', 'Bearer ' + localStorage.getItem(ACCESS_TOKEN))
        }
        return headers;
    }



    signup(signupRequest){
        return axios.post('http://localhost:8080/quizapp/api/signup',signupRequest,this.createHeaders);
    }

    login(loginRequest){
        return axios.post('http://localhost:8080/quizapp/api/login',loginRequest,this.createHeaders);
    }

    getCurrentUser(){
        return axios.get('http://localhost:8080/quizapp/api/currentuser',this.createHeaders);
    }

}


export default new Api()
