import axios from 'axios';
import { ACCESS_TOKEN } from '../common/constants';

 const headers = new Headers({
            'Content-Type': 'application/json',
            'Authorization':'Bearer ' + localStorage.getItem(ACCESS_TOKEN)
        })


class Api{
   


    signup(signupRequest){
        return axios.post('http://localhost:8080/quizapp/api/signup',signupRequest,{headers});
    }

    login(loginRequest){
        return axios.post('http://localhost:8080/quizapp/api/login',loginRequest,{headers});
    }

    getCurrentUser(){
        console.log(localStorage.getItem(ACCESS_TOKEN));
        return axios.get('http://localhost:8080/quizapp/api/currentuser',{headers});
    }

}


export default new Api()
