import { Button, Input,Form,Icon, notification } from 'antd';
import { Component } from 'react';
import { Link } from 'react-router-dom';
import './Login.css';
import  Api  from '../../api/Api';



class Login extends Component {
    constructor(props) {
        super(props);
        this.state={
            name:{
               value: ''
            },
            password:{
                value:''
            }
        }
        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

     
    handleSubmit =(values)=>  {

        const loginRequest = {
            name: this.state.name.value,
            password: this.state.password.value
        };

            

        console.log(values.name);
        console.log(values.password);
        //const loginRequest = Object.assign({}, values);
        Api.login(loginRequest)
        .then(response => {
            this.props.onLogin();
            console.log(response.data.message);
            }).catch(error => {
                if(error.response.status === 401) {
                    notification.error({
                        message: 'Quiz App',
                        description: 'Nazwa uzytkownika lub hasło nieprawidłowe.Spróbuj ponownie!'
                    });                    
                } else {
                    console.log('response: ', error.response.data);
                    notification.error({
                        message: 'Quiz App',
                        description: error.message || 'Błąd!'
                    });                                            
                }
        });
    }
    
    

    render() {
        

        
        return (
            <div className="login-container">
                <h1 className="login-title">Logowanie</h1>
                <div className="login-content">
                    <Form onFinish={this.handleSubmit} className="login-form">
                        <Form.Item name="name" rules={[{ required: true, message: 'Wpisz nazwę użytkownika!' }]}>
                            <Input 
                                prefix={<Icon type="user" />}
                                size="large"
                                name="name"
                                placeholder="Nazwa użytkownika"
                                value={this.state.name.value} 
                                onChange={(event) => this.handleInputChange(event)} />    
                            
                        </Form.Item>
                        <Form.Item name="password" rules={[{ required: true, message: 'Wpisz hasło!' }]}>
                            <Input 
                                prefix={<Icon type="lock" />}
                                size="large"
                                name="password"
                                type="password" 
                                placeholder="Hasło" 
                                value={this.state.name.value} 
                                onChange={(event) => this.handleInputChange(event)}/>                        
                            
                        </Form.Item>
                            <Form.Item>
                                <Button type="primary" htmlType="submit" size="large" onClick={this.handleSubmit}
                                className="login-button">Zaloguj się</Button>
                                lub <Link to="/signup">zarejestruj się!</Link>
                            </Form.Item>
                    </Form>
                </div>
            </div>
            
           
                    
              
        );
    }

    handleInputChange( event) {
    
        const inputName = event.target.name;        
        const inputValue = event.target.value;
        this.setState({
            [inputName] : {
                value: inputValue,
            }
        });

    }    
}


export default Login;
