import axios from 'axios';
import { ACCESS_TOKEN } from '../common/constants';

const createHeaders=()=>{
    const headers = new Headers({
            'Content-Type': 'application/json',
        })
    if(localStorage.getItem(ACCESS_TOKEN)) {
        headers.append('Authorization', 'Bearer ' + localStorage.getItem(ACCESS_TOKEN))
    }
    return headers;
}
 


class Api{
   


    signup(signupRequest){
        return axios.post('http://localhost:8080/quizapp/api/signup',signupRequest,{createHeaders});
    }

    login(loginRequest){
        return axios.post('http://localhost:8080/quizapp/api/login',loginRequest,{createHeaders});
    }

    getCurrentUser(){
        console.log({createHeaders});
        return axios.get('http://localhost:8080/quizapp/api/currentuser',{createHeaders});
    }

}


export default new Api()
