import { Button, Input,Form,Icon } from 'antd';
import { Component } from 'react';
import { Link } from 'react-router-dom';
import './Login.css';

class Login extends Component {
    render() {
        const MyLoginForm = Form.create()(LoginForm)
        return (
            <div className="login-container">
                <h1 className="login-title">Logowanie</h1>
                <div className="login-content">
                    <MyLoginForm onLogin={this.props.onLogin} />
                </div>
            </div>
        );
    }
}

class LoginForm extends Component {
    constructor(props) {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(event) {
        event.preventDefault();   
        const onFinish = values => {
             
                //const loginRequest = Object.assign({}, values);
                /*login(loginRequest)
                .then(response => {
                    localStorage.setItem(ACCESS_TOKEN, response.accessToken);
                    this.props.onLogin();
                }).catch(error => {
                    if(error.status === 401) {
                        notification.error({
                            message: 'Polling App',
                            description: 'Your Username or Password is incorrect. Please try again!'
                        });                    
                    } else {
                        notification.error({
                            message: 'Polling App',
                            description: error.message || 'Sorry! Something went wrong. Please try again!'
                        });                                            
                    }
                });*/
            
        }
    }

    render() {
        

        
        return (
            <Form onFinish={this.handleSubmit} className="login-form">
                <Form.Item name="name" rules={[{ required: true, message: 'Wpisz nazwę użytkownika!' }]}>
                     <Input 
                        prefix={<Icon type="user" />}
                        size="large"
                        placeholder="Nazwa użytkownika" />    
                    
                </Form.Item>
                <Form.Item name="password" rules={[{ required: true, message: 'Wpisz hasło!' }]}>
                    <Input 
                        prefix={<Icon type="lock" />}
                        size="large"
                        type="password" 
                        placeholder="Hasło"  />                        
                    
                </Form.Item>
                    <Form.Item>
                        <Button type="primary" htmlType="submit" size="large" onClick={this.handleSubmit}
                        className="login-button">Zaloguj się</Button>
                        lub <Link to="/signup">zarejestruj się!</Link>
                    </Form.Item>
            </Form>
            
           
                    
              
        );
    }
}


export default Login;
