import { Button, Divider, Form, Input, Select } from "antd";
import  {CloseOutlined, PlusOutlined } from '@ant-design/icons';

import TextArea from "antd/lib/input/TextArea";

import { Component } from "react";
import { QUIZ_CHOICE_MAX_LENGTH, QUIZ_MAX_CHOICES, QUIZ_NAME_MAX_LENGTH, QUIZ_QUESTION_MAX_LENGTH } from "../common/constants";
import './NewQuiz.css';  



class NewQuiz extends Component{
    constructor(props){
        super(props);
        this.state={
            quiz:{
                text:''
            },
            category:{
                text:''
            },
            questions:[{
                text:'',
                choiceOne:'',
                choiceTwo:'',
                choiceThree:'',
                choiceFour:''
            }]
            
           

        }

    this.handleQuizChange=this.handleQuizChange.bind(this);
    this.handleSubmit=this.handleSubmit.bind(this);
    this.handleCategoryChange=this.handleCategoryChange.bind(this);
    this.addChoice=this.addChoice.bind(this);
    this.removeQuestion=this.removeQuestion.bind(this);
    this.handleQuestionChange=this.handleQuestionChange.bind(this);
    this.handleChoiceOneChange=this.handleChoiceOneChange.bind(this);
    this.handleChoiceTwoChange=this.handleChoiceTwoChange.bind(this);
    this.handleChoiceThreeChange=this.handleChoiceThreeChange.bind(this);
    this.handleChoiceFourChange=this.handleChoiceFourChange.bind(this);

    }

    addChoice(event){
        const questions = this.state.questions.slice();
        this.setState({
            questions:questions.concat([{
                text:'',
                choiceOne:'',
                choiceTwo:'',
                choiceThree:'',
                choiceFour:''
            }])
        }); 
    }
    removeQuestion(questionNumber){
        const questions = this.state.questions.slice();
        this.setState({
            questions:[...questions.slice(0,questionNumber),...questions.slice(questionNumber+1)]
        });
    }

    handleQuizChange(event){
        const value=event.target.value;
        this.setState({
            quiz:{
                text:value,
                ...this.validateQuizName(value)
            }
        })

    }

    validateQuizName=(value)=>{
        if(value.length === 0) {
            return {
                validateStatus: 'error',
                errorMsg: 'Wpisz nazwę quizu!'
            }
        } else if (value.length > QUIZ_NAME_MAX_LENGTH) {
            return {
                validateStatus: 'error',
                errorMsg: `Nazwa quizu za długa (Maksymalnie ${QUIZ_NAME_MAX_LENGTH} znaków dozwolone)`
            }    
        } else {
            return {
                validateStatus: 'success',
                errorMsg: null
            }
        }
    }

    handleCategoryChange(value){
        this.setState({
            category:{
                text:value
            }
        });
    }

    handleQuestionChange(event,index){
        const questions = this.state.questions.slice();
        const value = event.target.value;

        questions[index] = {
            text: value,
            ...this.validateQuestion(value)
        }

        this.setState({
            questions: questions
        });

    }
    validateQuestion=(value)=>{
        if(value.length === 0) {
            return {
                validateStatus: 'error',
                errorMsg: 'Wpisz pytanie!'
            }
        } else if (value.length > QUIZ_QUESTION_MAX_LENGTH) {
            return {
                validateStatus: 'error',
                errorMsg: `Pytanie za długie (Maksymalnie ${QUIZ_QUESTION_MAX_LENGTH} znaków dozwolone)`
            }    
        } else {
            return {
                validateStatus: 'success',
                errorMsg: null
            }
        }
    }

    handleChoiceOneChange(event,index){
        const questions = this.state.questions.slice();
        const value = event.target.valu;

        questions[index] = {
            choiceOne: value,
            ...this.validateChoice(value)
        }

        this.setState({
            questions: questions
        });
    }

    handleChoiceTwoChange(event,index){
        const questions = this.state.questions.slice();
        const value = event.target.valu;

        questions[index] = {
            choiceTwo: value,
            ...this.validateChoice(value)
        }

        this.setState({
            questions: questions
        });
    }
    handleChoiceThreeChange(event,index){
        const questions = this.state.questions.slice();
        const value = event.target.valu;

        questions[index] = {
            choiceThree: value,
            ...this.validateChoice(value)
        }

        this.setState({
            questions: questions
        });
    }
    handleChoiceFourChange(event,index){
        const questions = this.state.questions.slice();
        const value = event.target.valu;

        questions[index] = {
            choiceFour: value,
            ...this.validateChoice(value)
        }

        this.setState({
            questions: questions
        });
    }

    validateChoice=(value)=>{
        if(value.length === 0) {
            return {
                validateStatus: 'error',
                errorMsg: 'Wpisz odpowiedź!'
            }
        } else if (value.length > QUIZ_CHOICE_MAX_LENGTH) {
            return {
                validateStatus: 'error',
                errorMsg: `Odpowiedź (Maksymalnie ${QUIZ_CHOICE_MAX_LENGTH} znaków dozwolone)`
            }    
        } else {
            return {
                validateStatus: 'success',
                errorMsg: null
            }
        }
    }


    handleSubmit(event){

    }



    render(){
        const questionViews=[];
        this.state.questions.forEach((question,index)=>{
            questionViews.push(<QuizQuestion  key={index} question={question} questionNumber={index}
            removeQuestion={this.removeQuestion} handleQuestionChange={this.handleQuestionChange}
            handleChoiceOneChange={this.handleChoiceOneChange} handleChoiceTwoChange={this.handleChoiceTwoChange}
            handleChoiceThreeChange={this.handleChoiceThreeChange} handleChoiceFourChange={this.handleChoiceFourChange}/>);
        });

        return(
            <div className="new-quiz-container">
                <h1 className="title">Nowy Quiz</h1>
                <div className="new-quiz-content">
                    <Form onFinish={this.handleSubmit} className="new-quiz-form">
                        <Form.Item validateStatus={this.state.quiz.validateStatus}
                        help={this.state.quiz.errorMsg} className="form-row">
                            <TextArea
                            placeholder="Wprowadź nazwę quizu"
                            style = {{ fontSize: '16px' }} 
                            autosize={{ minRows: 3, maxRows: 6 }} 
                            name = "quiz"
                            value = {this.state.quiz.text}
                            onChange = {this.handleQuizChange}/>
                        </Form.Item>
                        <Form.Item className="form-row">
                            
                            Kategoria:<Select name="categories"
                            defaultValue="geografia"
                            value={this.state.category.text}
                            onChange={this.handleCategoryChange}
                            style={{width:150}}>
                                <Select.Option value="geografia">geografia</Select.Option>
                                <Select.Option value="język">język</Select.Option>
                                <Select.Option value="historia">historia</Select.Option>
                                <Select.Option value="przyroda">przyroda</Select.Option>
                                <Select.Option value="filmy">filmy</Select.Option>
                                <Select.Option value="książki">książki</Select.Option>
                                <Select.Option value="wiedza ogólna">wiedza ogólna</Select.Option>
                                <Select.Option value="matematyka">matematyka</Select.Option>
                            </Select>
                        </Form.Item>    
                        <Divider className="line"/>
                        {questionViews}

                        <Form.Item className="form-row">
                            <Button type="dashed" onClick={this.addChoice} disabled={this.state.questions.length === QUIZ_MAX_CHOICES}>
                                <PlusOutlined /> Dodaj pytanie
                            </Button>
                        </Form.Item>

                        
                        <Form.Item className="form-row">
                            <Button type="primary" 
                                htmlType="submit" 
                                size="large" 
                                //disabled={this.isFormInvalid()}
                                className="create-quiz-button">Utwórz quiz</Button>
                        </Form.Item>
                       


                    </Form>
                </div>
            </div>
        );
    }

}

function QuizQuestion(props){
    return(
        <Form.Item validateStatus={props.question.validateStatus}
        help={props.question.errorMsg} className="form-row">
            <TextArea
                placeholder={'Pytanie ' + (props.questionNumber + 1)}
                style = {{ fontSize: '16px' }} 
                autosize={{ minRows: 2, maxRows: 4 }} 
                name = "question"
                value = {props.question.text} 
                onChange={(event)=>props.handleQuestionChange(event,props.questionNumber)}/>
            

            {
                props.questionNumber > 0 ? (
                <CloseOutlined
                    className="dynamic-delete-button"
                    disabled={props.questionNumber < 1}
                    onClick={() => props.removeQuestion(props.questionNumber)}
                /> ): null
            } 
            <Input 
                placeholder = 'Odpowiedź 1 ' 
                size="large"
                value={props.question.choiceOne} 
                className="choice-input"
                onChange={(event) => props.handleChoiceOneChange(event, props.questionNumber)} />
            <Input 
                placeholder = 'Odpowiedź 2 ' 
                size="large"
                className="choice-input"
                value={props.question.choiceTwo} 
                onChange={(event) => props.handleChoiceTwoChange(event, props.questionNumber)} />
            <Input 
                placeholder = 'Odpowiedź 3 ' 
                size="large"
                value={props.question.choiceThree} 
                className="choice-input"
                onChange={(event) => props.handleChoiceThreeChange(event, props.questionNumber)} />
            <Input 
                placeholder = 'Odpowiedź 4 ' 
                size="large"
                className="choice-input"
                value={props.question.choiceFour} 
                onChange={(event) => props.handleChoiceFourChange(event, props.questionNumber)} />
            
       
            

        </Form.Item>  
        

    );
}




export default NewQuiz;
